import { React, useEffect, useState } from 'react';
import './Contacts.css'

const Contacts = () => {
  const [contacts, setContacts] = useState([]);
  const [selected, setSelected] = useState(null);

  useEffect(() => {
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(res => res.json())
      .then(json => {
        setContacts(json.sort((a, b) => a.name.localeCompare(b.name)));
      });
  }, []);

  if (selected) {
    return (
      <div className="Body">
        <button className="back-button" onClick={() => setSelected(null)}>‚Üê Back</button>
        <div className="detail-card">
          <h2>{selected.name}</h2>
          <p><strong>Email:</strong> {selected.email}</p>
          <p><strong>Phone:</strong> {selected.phone}</p>
          <p><strong>Username:</strong> {selected.username}</p>
          <p><strong>Website:</strong> {selected.website}</p>
          <p><strong>Company:</strong> {selected.company.name}</p>
          <p><strong>Catchphrase:</strong> {selected.company.catchPhrase}</p>
          <p><strong>Address:</strong> {`${selected.address.suite}, ${selected.address.street}, ${selected.address.city}, ${selected.address.zipcode}`}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="Body">
      <h1>Contacts</h1>
      <div className="contacts-grid">
        {contacts.map(user => (
          <div className="card" key={user.id} onClick={() => setSelected(contact)}>
            <h2>{user.name}</h2>
            <p>Email: {user.email}</p>
            <p>Phone: {user.phone}</p>
            <p>Company: {user.company.name}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Contacts
